import React from 'react'
import { number, shape } from 'prop-types'
import injectSheet, { jss, ThemeProvider } from 'react-jss'
import jssNested from 'jss-nested'

jss.use(jssNested())

const getapperLogo = 'data:image/png;base64,'


const styles = (theme) => {
  const { radius } = theme
  const size = radius * 0.4
  const width = Math.max(200, radius * 2)

  return ({
    '@keyframes spin': {
      from: {
        transform: 'rotate(0deg)'
      },
      to: {
        transform: 'rotate(360deg)'
      }
    },
    '@keyframes spinback': {
      from: {
        transform: 'rotate(0deg)'
      },
      to: {
        transform: 'rotate(-360deg)'
      }
    },
    wrapper: {
      position: 'fixed',
      bottom: 0,
      right: -width + radius,
      height: radius,
      width,
      background: theme.background,
      boxShadow: '5px 5px 15px 2px #33333333',
      borderTopLeftRadius: radius,
      textAlign: 'left',
      boxSizing: 'border-box',
      padding: {
        left: radius / 2.5,
        right: radius / 2.5,
        top: radius * 0.15
      },
      display: 'flex',
      alignItems: 'center',
      transition: 'right ease-out .33s',
      '&:hover': {
        right: 0,
        '& $logo': {
          animation: {
            name: 'spinback',
            duration: '.5s'
          }
        }
      }
    },
    logo: {
      width: 'auto',
      height: size,
      margin: {
        right: radius * 0.3
      },
      animation: {
        name: 'spin',
        duration: '.5s'
      }
    },
    title: {
      font: {
        size: 40,
        weight: 900
      },
      color: theme.color
    },
    link: {
      display: 'flex',
      alignItems: 'center',
      color: theme.color,
      '&:hover': {
        opacity: 0.5
      }
    }
  })
}

const Comp = ({ classes }) => (
  <div className={classes.wrapper}>
    <a
      className={classes.link}
      href="http://getapper.com"
      target="_blank"
      rel="noopener noreferrer"
    >
      <img
        alt="Getapper"
        className={classes.logo}
        src={getapperLogo}
      />
      <span>getapper.com</span>
    </a>
  </div>
)

Comp.propTypes = {
  classes: shape({}).isRequired
}

const StyledComp = injectSheet(styles)(Comp)

const theme = radius => ({
  background: '#fff',
  color: '#24292e',
  radius
})

const App = ({
  radius
}) => (
  <ThemeProvider theme={theme(radius)}>
    <StyledComp />
  </ThemeProvider>
)

App.propTypes = {
  radius: number
}

App.defaultProps = {
  radius: 75
}

export default App
